import logo from './logo.svg';
import './App.css';
import web3 from './web3'
import lottery from './lottery'
import {useState,useEffect} from 'react'

function App() {

  const [players, setPlayers] = useState(0) ;
  const [balance,setBalance] = useState(0) ;
  const [manager,setManager] = useState('') ;



  useEffect(() =>{
    const fetchPlayers = async () => {
        const playerss = await lottery.methods.getPlayers().call() ;
        setPlayers(playerss.length);
    }

    const fetchBalance = async () => {
      const bal = await lottery.methods.getBalance().call() ;
      const inEther = web3.utils.fromWei(bal,'ether') ;
      setBalance(inEther);
    }

    const fetchManager = async () => {
         const man = await lottery.methods.manager().call() ;
         setManager(man) ;
    }

    fetchPlayers();
    fetchBalance();
    fetchManager();

  },[])

  return (
   <div>
     <h2>Wanna get lucky? Enter into the lottery !!</h2>
     <h4>This contract is managed by {manager}</h4>
     <h4>{players} players are competing to win {balance} ether</h4>
     <hr></hr>
     <form>
       <label>Enter the lottery </label>
       <input></input>
     </form>
   </div>
  );
}

export default App;
