import logo from './logo.svg';
import './App.css';
import web3 from './web3'
import lottery from './lottery'
import {useState,useEffect} from 'react'

function App() {

  const [players, setPlayers] = useState(0) ;
  const [balance,setBalance] = useState(0) ;
  const [manager,setManager] = useState('') ;
  const [value,setValue] = useState(0);
  const [message,setMessage] = useState('');



  useEffect(() =>{
    const fetchPlayers = async () => {
        const playerss = await lottery.methods.getPlayers().call() ;
        setPlayers(playerss.length);
    }

    const fetchBalance = async () => {
      const bal = await lottery.methods.getBalance().call() ;
      const inEther = web3.utils.fromWei(bal,'ether') ;
      setBalance(inEther);
    }

    const fetchManager = async () => {
         const man = await lottery.methods.manager().call() ;
         setManager(man) ;
    }

    fetchPlayers();
    fetchBalance();
    fetchManager();

  },[])

  const onSubmit = async (event) => {
    event.preventDefault() ;

    setMessage('Waiting for the transaction to get confirmed ') ;
    const accounts = await web3.eth.getAccounts() ;

    await lottery.methods.enter().send({from : accounts[0], value:web3.utils.toWei(value,'ether')});

    setMessage('Kudos! You have entered the lottery') ;

  }

  const pickW = async () => {
    const accounts = await web3.eth.getAccounts() ;

    setMessage('We are picking a winner') ;


    await lottery.methods.pickW().send({from:accounts[0]}); 

    setMessage('Winner has been picked!') ;
  }

  return (
   <div>
     <h2>Wanna get lucky? Enter into the lottery !!</h2>
     <h4>This contract is managed by {manager}</h4>
     <h4>{players} player(s) are competing to win {balance} ether</h4>
     <hr></hr>
     <form onSubmit={onSubmit}>
       <label>Enter the lottery </label>
       <input value={value} onChange = {event => setValue(event.target.value)} />
       <button>Submit</button>
     </form>
     <h3>{message}</h3>
     <hr></hr>

     <button onClick={pickW}>Pick a Winner !</button>
     <h3>{message}</h3>
   </div>
  );
}

export default App;
